package com.syntax.class03;

public class AdditionVersusConceratation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=100;
		int b=200;
		
		String x="Hello";
		String y="Friends";
		
		System.out.println(a+b+x+y);//addition
		System.out.println(x+y+a+b);//concatenation 
		System.out.println(x+y+(a+b));
		
		

	}

}
